            <script>
                var quantity = 1;

                function updateInput() {
                    $('.quantityInput').val(quantity);
                    console.log(quantity);
                }

                $('#increase').on('click', function() {
                    quantity++;
                    updateInput();
                })

                $('#decrease').on('click', function() {
                    if (quantity > 1) {
                        quantity--;
                        updateInput();
                    }
                })

                $('.quantityInput').on('input', function() {
                    let val = parseInt($(this).val());
                    if (!isNaN(val) && val >= 1) {
                        quantity = val;
                    } else {
                        quantity = 1;
                    }
                    updateInput();
                });

                

            </script>

